# -*- coding: utf-8 -*-
from __future__ import division

peso = input('Digite o peso : ')
altura = input('Digite a altura: ')

imc = peso/(altura**2)

if imc<20:
    print('ABAIXO')
elif 20<=imc<=25:
    print('NORMAL')
elif 25<=imc<=30:
    print('SOBREPESO')
elif 30<imc<=40:
    print('OBESIDADE')
else:
    print('OBESIDADE GRAVE')

